import { useEffect, useState } from "react";

import Sidebar from "../Sidebar/Sidebar";
import mockEntries from '../../mockData/entries.mock';

import './Home.css';

const Home = () => {
    const [entries, setEntries] = useState(mockEntries);
    const [activeEntry, setActiveEntry] = useState(null);

    useEffect(() => {
        const activeEntryId = localStorage.getItem('activeEntry');
        const activeEntryObj = mockEntries.find(e => e.id == activeEntryId);

        setActiveEntry(activeEntryObj);
    }, []);

    return (
        <section className="content-wrapper">
            <Sidebar entries={entries} activeEntryId={activeEntry.id}></Sidebar>
            {activeJournal &&
                <article className="entry-content">
                    <h1>{activeJournal.date}</h1>
                    <p>{activeJournal.content}</p>
                </article>
            }

        </section>
    );
};

export default Home;
