import events from '../../mockData/entries.mock';
import { getDay, getDate } from '../../utils';

import './Sidebar.css';

const Sidebar = () => {

    return (
        <article>
            <h1 className="month-title">November 2022</h1>
            {events.map(ev => 
                <section className={`entry ${ev.isActive ? 'active' : ''}`}>
                    <div className="date-card">
                        <p className="day">{getDay(ev.date)}</p>
                        <h4 className="date">{getDate(ev.date)}</h4>
                    </div>

                    <div className="content-short">
                        <p>{ev.content.slice(0, 80)}...</p>
                    </div>
                </section>
            )}
        </article>
    );
};

export default Sidebar;
