import { useState, useEffect } from 'react';

import './OptionsMenu.css';

const OptionsMenu = () => {
    const [menuIsDisplayed, setMenuIsDisplayed] = useState(false);

    useEffect(() => {
        window.addEventListener('contextmenu', e => {
            setMenuIsDisplayed(true);
        })
    }, []);

    return (
        <ul className={`options-menu ${menuIsDisplayed ? 'menu-active' : ''}`}>
            <li className="options-menu-item">Test</li>
            <li className="options-menu-item">Delete</li>
        </ul>
    );
};

export default OptionsMenu;
