import { useEffect, useState } from "react";
import { getFullDate } from "../../utils";
import { entriesService } from "../../services/entriesService";

import Sidebar from "../Sidebar/Sidebar";

import './Home.css';

const Home = () => {
    const [entries, setEntries] = useState([]);
    const [activeEntry, setActiveEntry] = useState(null);

    useEffect(() => {
        entriesService.getAll()
            .then(res => {
                setEntries(res);

                const latestEntry = res.find(e => e.id == 1);
                setActiveEntry(latestEntry);
            })
            .catch(err => {
                console.alert(err);
            })
    }, []);

    return (
        <section className="content-wrapper">
            <Sidebar entries={entries} activeEntry={activeEntry} setActiveEntry={setActiveEntry}></Sidebar>
            {activeEntry &&
                <article className="entry-content-wrapper">
                    <section className="entry-content">
                        <input type="hidden" name={activeEntry.id} />
                        <h1 className="entry-date">{getFullDate(activeEntry.createdAt)}</h1>
                        <textarea className="entry-text">{activeEntry.content}</textarea>
                    </section>

                    <button className="save-entry-btn">Save</button>
                </article>
            }
        </section>
    );
};

export default Home;
