import { baseUrl } from '../constants';

const getByEntryId = async (entryId) => {
    const response = await fetch(`${baseUrl}/images/${entryId}`);
    const data = await response.json();

    if (!response.ok) {
        throw new Error(data);
    }

    return data;
};

const postImages = async (imgs) => {
    const formData = new FormData();

    const imgArr = Object.values(imgs);

    imgArr.forEach((img, i) => {
        formData.append(`File${i}`, img);
    });

    console.log(formData.values());
    // const response = await fetch(`${baseUrl}/images`, {

    // });


}

export const imagesService = { getByEntryId, postImages };
