import { useState, useEffect } from 'react';

import { UilTrash } from '@iconscout/react-unicons'

import './OptionsMenu.css';

const OptionsMenu = (props) => {
    const [menuIsDisplayed, setMenuIsDisplayed] = useState(false);

    const entryId = props.entryId;

    useEffect(() => {
        const optionsMenu = document.getElementById(id);

        document.addEventListener('contextmenu', e => {
            e.preventDefault();

            if (e.target.className == 'entry') {
                setMenuIsDisplayed(true);

                const xPosition = e.clientX;
                const yPosition = e.clientY;

                optionsMenu.style.left = `${xPosition}px`;
                optionsMenu.style.top = `${yPosition}px`;
            }
        });

        document.addEventListener('click', () => {
            setMenuIsDisplayed(false);
        })
    }, [menuIsDisplayed]);

    const deleteEntry = (event) => {
        event.preventDefault();
        const entryId = event.currentTarget.parentElement.parentElement.querySelector('input');
        console.log(entryId);
    }

    return (
        <ul className={`options-menu ${menuIsDisplayed ? 'menu-active' : ''}`} id={entryId}>
            <li className="options-menu-item" onClick={e => deleteEntry(e)}>
                <UilTrash size="25" className="options-menu-icon"/>
                <span>Delete</span>
            </li>

            <li className="options-menu-item">
                <UilTrash size="25" className="options-menu-icon"/>
                <span>Test</span>
            </li>
        </ul>
    );
};

export default OptionsMenu;
