import { useState } from 'react';
import mockEntries from '../../mockData/entries.mock';
import { getDay, getDate } from '../../utils';

import './Sidebar.css';

const Sidebar = () => {
    const [entries, setEntries] = useState(mockEntries);

    const openEntry = (event, entryId) => {
        const prevActiveEntry = document.querySelector('.active');

        if (prevActiveEntry) {
            prevActiveEntry.classList.remove('active');
        }

        const currentEntry = event.currentTarget;
        currentEntry.classList.add('active');

        localStorage.setItem('activeEntry', entryId);
        window.dispatchEvent(new Event('storage'));
    };

    return (
        <article className="sidebar">
            {entries.map((entry, i) => 
                <section className={i === 0 ? 'entry active' : 'entry'} onClick={e => openEntry(e, entry.id)}>
                    <div className="date-card">
                        <h6 className="day">{getDay(entry.date)}</h6>
                        <h4 className="date">{getDate(entry.date)}</h4>
                    </div>

                    <div className="content-short">
                        <p>{entry.content.slice(0, 85)}...</p>
                    </div>
                </section>
            )}
        </article>
    );
};

export default Sidebar;
