import { getDay, getDayNum } from '../../utils';

import './Sidebar.css';

const Sidebar = (props) => {
    const entries = props.entries;
    const activeEntry = props.activeEntry;
    const activeEntryId = props.activeEntryId;
    const setActiveEntry = props.setActiveEntry;
    const setActiveEntryId = props.setActiveEntryId;

    return (
        <article className="sidebar">
            {activeEntry && 
                <>
                {entries.map((entry, i) => 
                <>
                    {entry.id}
                    <section className={(i + 1) == activeEntryId ? 'entry active' : 'entry'} onClick={e => setActiveEntryId(entry.id)}>
                        <p>{activeEntryId}</p>
                        <div className="date-card">
                            <h6 className="day">{getDay(entry.createdAt)}</h6>
                            <h4 className="date">{getDayNum(entry.createdAt)}</h4>
                        </div>

                        <div className="content-short">
                            <p>{entry.content?.slice(0, 85)}...</p>
                        </div>
                    </section>
                </>
                )}
                </>
            }

        </article>
    );
};

export default Sidebar;
