import { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faEllipsis, faXmark } from '@fortawesome/free-solid-svg-icons';

import { getFullDate } from "../../utils";
import { entriesService } from "../../services/entriesService";

import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

import Sidebar from "../Sidebar/Sidebar";

import './Home.css';

const MySwal = withReactContent(Swal);

const Home = () => {
    const [entries, setEntries] = useState([]);
    const [activeEntryId, setActiveEntryId] = useState();
    const [activeEntry, setActiveEntry] = useState(null);
    const [activeEntryContent, setActiveEntryContent] = useState(null);

    useEffect(() => {
        entriesService.getAll()
            .then(res => {
                setEntries(res);

                const entriesIds = res
                    .map(e => e.id)
                    .sort((a, b) => a - b);

                setActiveEntryId(entriesIds[0]);

                entriesService.getOne(entriesIds[0])
                    .then(res => {
                        console.log(res);
                        setActiveEntry(res);
                        setActiveEntryContent(res.content);
                    })
                    .catch(err => {
                        console.log(err);
                    });
                    })
            .catch(err => {
                console.alert(err);
            });

    }, []);

    // useEffect(() => {

    // }, [activeEntryId]);

    const updateEntry = (event) => {
        event.preventDefault();

        const formData = new FormData(event.target);

        const entryId = formData.get('entry-id');
        const content = formData.get('content');

        entriesService.updateEntry(entryId, content)
            .then(() => {
                MySwal.fire({
                    icon: 'success',
                    title: <p>Entry saved successfully</p>,
                    didOpen: () => {
                        MySwal.clickConfirm()
                    }
                }).then(() => MySwal.fire('Entry saved successfully', '', 'success'));
            })
            .catch(() => {
                MySwal.fire({
                    icon: 'error',
                    title: <p>Cannot save your entry.</p>,
                    didOpen: () => {
                        MySwal.clickConfirm()
                    }
                }).then(() => MySwal.fire('Cannot save your entry.', '', 'error'));
            }); 


    }

    const createEntry = (event) => {
        event.preventDefault();

        entriesService.createEntry()
            .then(res => {
                setActiveEntryId(res.id);
                setActiveEntryContent('');
            })
            .catch(err => {
                console.log(err);
            });
    } 

    return (
        <section className="content-wrapper">
            <Sidebar entries={entries} activeEntry={activeEntry} activeEntryId={activeEntryId} setActiveEntryId={setActiveEntryId}></Sidebar>
            {activeEntry &&
                <article>
                    <section className="entry-settings">
                        <article className="icons-group-1">
                            <FontAwesomeIcon icon={faXmark} className="icon x-icon"></FontAwesomeIcon>
                        </article>

                        <article className="icons-group-2">
                            <FontAwesomeIcon icon={faEllipsis} className="icon menu-icon"></FontAwesomeIcon>
                            <FontAwesomeIcon icon={faPlus} onClick={e => createEntry(e)} className="icon plus-icon"></FontAwesomeIcon>
                        </article>
                    </section>

                    <form className="entry-wrapper" onSubmit={(e) => updateEntry(e)}>
                        <section className="entry-content-wrapper">
                            <input type="hidden" className="entry-id" name="entry-id" defaultValue={activeEntry.id} />
                            <h1 className="entry-date">{getFullDate(activeEntry.createdAt)}</h1>
                            <textarea className="entry-content" name="content" value={activeEntryContent} onChange={e => setActiveEntryContent(e.target.value)}></textarea>
                        </section>

                        <button className="save-entry-btn" type="submit">Save</button>
                    </form>
                </article>
            }
        </section>
    );
};

export default Home;
