import events from '../../mockData/entries.mock';
import { getDay, getDate } from '../../utils';

const Sidebar = () => {

    return (
        <article>
            {events.map(ev => 
                <section className="entry">
                    <div className="date-card">
                        <p>{getDay(ev.date)}</p>
                        <h4>{getDate(ev.date)}</h4>
                    </div>

                    <div className="content-short">

                    </div>
                </section>
            )}
        </article>
    );
};

export default Sidebar;
