export const getDay = (date) => {
    const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

    const currentDate = new Date(date);
    const dayIndex = currentDate.getDay();

    const dayNum = currentDate.getDate();
    console.log(dayNum);

    return days[dayIndex - 1];
}
