import { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faEllipsis, faXmark } from '@fortawesome/free-solid-svg-icons';

import { getFullDate } from "../../utils";
import { entriesService } from "../../services/entriesService";

import Sidebar from "../Sidebar/Sidebar";

import './Home.css';

const Home = () => {
    const [entries, setEntries] = useState([]);
    const [activeEntryId, setActiveEntryId] = useState(null);
    const [activeEntry, setActiveEntry] = useState(null);

    useEffect(() => {
        entriesService.getAll()
            .then(res => {
                setEntries(res);

                if (activeEntryId) {
                    setActiveEntry(res.find(e => e.id == activeEntryId));
                } else {
                    setActiveEntry(res[0]);
                }
            })
            .catch(err => {
                console.alert(err);
            })
    }, [activeEntryId, activeEntry]);

    const updateEntry = (event) => {
        event.preventDefault();

        const formData = new FormData(event.target);

        const entryId = formData.get('entry-id');
        const content = formData.get('content');

        entriesService.updateEntry(entryId, content)
            .then(res => {
                console.log(res);
            })
            .catch(err => {
                console.log(err);
            }); 
    }

    const createEntry = (event) => {
        event.preventDefault();

        entriesService.createEntry()
            .then(res => {
                console.log(res);
            })
            .catch(err => {
                console.log(err);
            });
    } 


    return (
        <section className="content-wrapper">
            <Sidebar entries={entries} activeEntry={activeEntry} setActiveEntry={setActiveEntry} activeEntryId={activeEntryId} setActiveEntryId={setActiveEntryId}></Sidebar>
            {activeEntry &&
                <article>
                    <section className="entry-settings">
                        <article className="icons-group-1">
                            <FontAwesomeIcon icon={faXmark}></FontAwesomeIcon>
                        </article>

                        <article className="icons-group-2">
                            <FontAwesomeIcon icon={faEllipsis}></FontAwesomeIcon>
                            <FontAwesomeIcon icon={faPlus} onClick={e => createEntry(e)}></FontAwesomeIcon>
                        </article>
                    </section>

                    <form className="entry-wrapper" onSubmit={(e) => updateEntry(e)}>
                        <section className="entry-content-wrapper">
                            <input type="hidden" className="entry-id" name="entry-id" defaultValue={activeEntry.id} />
                            <h1 className="entry-date">{getFullDate(activeEntry.createdAt)}</h1>
                            <textarea className="entry-content" name="content" defaultValue={activeEntry.content}></textarea>
                        </section>

                        <button className="save-entry-btn" type="submit">Save</button>
                    </form>
                </article>
            }
        </section>
    );
};

export default Home;
