import { useState, useEffect } from 'react';
import { getDay, getDate } from '../../utils';

import './Sidebar.css';

const Sidebar = (props) => {
    const entries = props.entries;
    const activeEntry = props.activeEntry;
    const setActiveEntry = props.setActiveEntry;

    const openEntry = (event, entryId) => {
        const prevActiveEntry = document.querySelector('.active');

        if (prevActiveEntry) {
            prevActiveEntry.classList.remove('active');
        }

        const currentEntry = event.currentTarget;
        currentEntry.classList.add('active');

        const entrySelected = entries.find(e => e.id == entryId);
        setActiveEntry(entrySelected)
    };

    return (
        <article className="sidebar">
            {entries.map((entry, i) => 
                <section className={(i + 1) == activeEntryId ? 'entry active' : 'entry'} onClick={e => openEntry(e, entry.id)}>
                    <div className="date-card">
                        <h6 className="day">{getDay(entry.date)}</h6>
                        <h4 className="date">{getDate(entry.date)}</h4>
                    </div>

                    <div className="content-short">
                        <p>{entry.content.slice(0, 85)}...</p>
                    </div>
                </section>
            )}
        </article>
    );
};

export default Sidebar;
