import { useState, useEffect } from 'react';

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from '@fortawesome/free-solid-svg-icons';

import { UilTrash } from '@iconscout/react-unicons'

import './OptionsMenu.css';

const OptionsMenu = () => {
    const [menuIsDisplayed, setMenuIsDisplayed] = useState(false);

    useEffect(() => {
        const optionsMenu = document.querySelector('.options-menu');

        document.addEventListener('contextmenu', e => {
            e.preventDefault();

            const xPosition = e.clientX;
            const yPosition = e.clientY;

            optionsMenu.style.left = `${xPosition}px`;
            optionsMenu.style.top = `${yPosition}px`;
            setMenuIsDisplayed(true);
        });

        document.addEventListener('click', () => {
            setMenuIsDisplayed(false);
        })
    }, [menuIsDisplayed]);

    return (
        <ul className={`options-menu ${menuIsDisplayed ? 'menu-active' : ''}`}>
            <li className="options-menu-item">
                <UilTrash size="25" className="options-menu-icon"/>
                <span>Delete</span>
            </li>

            <li className="options-menu-item">
                <UilTrash size="25" className="options-menu-icon"/>
                <span>Test</span>
            </li>
        </ul>
    );
};

export default OptionsMenu;
