import { useEffect, useState } from "react";
import { getDate } from "../../utils";

import Sidebar from "../Sidebar/Sidebar";
import mockEntries from '../../mockData/entries.mock';

import './Home.css';

const Home = () => {
    const [entries, setEntries] = useState(mockEntries);
    const [activeEntry, setActiveEntry] = useState(null);

    useEffect(() => {
        const latestEntry = entries.find(e => e.id == 1);
        setActiveEntry(latestEntry);
    }, []);

    return (
        <section className="content-wrapper">
            <Sidebar entries={entries} activeEntry={activeEntry} setActiveEntry={setActiveEntry}></Sidebar>
            {activeEntry &&
                <article className="entry-content">
                    <h1 className="entry-date">{activeEntry.date}</h1>
                    <p className="entry-text">{activeEntry.content}</p>
                </article>
            }
        </section>
    );
};

export default Home;
