import { useEffect, useState } from "react";
import Sidebar from "../Sidebar/Sidebar";
import mockEntries from '../../mockData/entries.mock';

import './Home.css';

const Home = () => {
    const [activeJournal, setActiveJournal] = useState(null);

    useEffect(() => {
        const activeEntryId = localStorage.getItem('activeEntry');
        const activeEntry = mockEntries.find(e => e.id == activeEntryId);
        setActiveJournal(activeEntry);
    }, []);

    return (
        <section className="content-wrapper">
            <Sidebar></Sidebar>
            {activeJournal &&
                <>
                    <h1>{activeJournal.date}</h1>
                    <p>{activeJournal.content}</p>
                </>
            }

        </section>
    );
};

export default Home;
